import { getAllDictList, addDict, editDict, deleteDict, searchDict, getAllDictDataList, addDictData, editDictData, deleteData } from "./api.js";
export default {
  name: "dic-manage",
  data() {
    return {
      selected: ["选择", "编号", "所属字典", "键", "值", "描述", "排序", "是否禁用", "操作"],
      treeLoading: false,
      // 树加载状态
      maxHeight: "500px",
      loading: false,
      // 表格加载状态
      editTitle: "",
      // 编辑节点名称
      searchKey: "",
      // 搜索树
      expand: true,
      span: 18,
      expandIcon: "md-arrow-round-back",
      selectNode: {},
      treeData: [],
      // 树数据
      selectCount: 0,
      // 多选计数
      selectList: [],
      // 多选数据
      searchForm: {
        // 搜索框对应data对象
        name: "",
        status: "",
        pageNumber: 1,
        // 当前页数
        pageSize: 10,
        // 页面大小
        sort: "sortOrder",
        // 默认排序字段
        order: "asc" // 默认排序方式
      },
      modalType: 0,
      // 添加或编辑标识
      modalVisible: false,
      // 添加或编辑显示
      dictModalVisible: false,
      dictModalTitle: "",
      modalTitle: "",
      // 添加或编辑标题
      dictForm: {
        title: "",
        type: "",
        description: "",
        sortOrder: 0
      },
      form: {
        // 添加或编辑表单对象初始化数据
        title: "",
        value: "",
        status: 0,
        description: "",
        sortOrder: 0
      },
      dictFormValidate: {
        // 表单验证规则
        title: [{
          required: true,
          message: "不能为空",
          trigger: "blur"
        }],
        type: [{
          required: true,
          message: "不能为空",
          trigger: "blur"
        }],
        sortOrder: [{
          required: true,
          type: "number",
          message: "排序值不能为空",
          trigger: "blur"
        }]
      },
      formValidate: {
        // 表单验证规则
        title: [{
          required: true,
          message: "不能为空",
          trigger: "blur"
        }],
        value: [{
          required: true,
          message: "不能为空",
          trigger: "blur"
        }],
        sortOrder: [{
          required: true,
          type: "number",
          message: "排序值不能为空",
          trigger: "blur"
        }]
      },
      columns: [{
        title: "选择",
        type: "selection",
        minWidth: 60,
        align: "center"
      }, {
        title: "编号",
        type: "index",
        minWidth: 90,
        align: "center"
      }, {
        title: "所属字典",
        key: "dictName",
        minWidth: 120,
        sortable: true
      }, {
        title: "键",
        key: "title",
        minWidth: 180,
        sortable: true
      }, {
        title: "值",
        key: "value",
        minWidth: 180,
        sortable: true
      }, {
        title: "描述",
        key: "description",
        minWidth: 120,
        sortable: true
      }, {
        title: "排序",
        key: "sortOrder",
        minWidth: 90,
        sortable: true,
        sortType: "asc"
      }, {
        title: "是否禁用",
        key: "status",
        align: "center",
        minWidth: 120,
        render: (h, params) => {
          return h("div", [h("Tag", {
            props: {
              color: params.row.status == 0 ? "green" : "red",
              size: "medium"
            }
          }, params.row.status == 0 ? "启用" : "禁用")]);
        }
      }, {
        title: "创建时间",
        key: "createTime",
        minWidth: 180,
        sortable: true
      }, {
        title: "操作",
        key: "action",
        width: 180,
        align: "center",
        fixed: "right",
        render: (h, params) => {
          var that = this;
          return h("div", [h("Button", {
            props: {
              type: "primary",
              size: "small",
              ghost: true,
              icon: "ios-brush",
              disabled: !(that.$route.meta.permTypes && that.$route.meta.permTypes.includes("delete"))
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.edit(params.row);
              }
            }
          }, "编辑"), h("Button", {
            props: {
              type: "error",
              size: "small",
              ghost: true,
              icon: "md-close",
              disabled: !(that.$route.meta.permTypes && that.$route.meta.permTypes.includes("delete"))
            },
            on: {
              click: () => {
                this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      submitLoading: false,
      // 添加或编辑提交状态
      data: [],
      //表单数据
      total: 0,
      // 表单数据总数
      selectRow: {}
    };
  },
  methods: {
    init() {
      // 获取树数据
      this.getAllDict();
      // 获取表单数据
      this.getDataList();
    },
    rowClick(row, index) {
      this.selectRow = row;
    },
    rowClassName(row, index) {
      if (row.id == this.selectRow.id) {
        return "rowClassNameColor";
      }
      return "";
    },
    getAllDict() {
      this.treeLoading = true;
      getAllDictList().then(res => {
        this.treeLoading = false;
        if (res.success) {
          this.treeData = res.result;
        }
      });
    },
    search() {
      // 搜索树
      if (this.searchKey) {
        this.treeLoading = true;
        searchDict({
          key: this.searchKey
        }).then(res => {
          this.treeLoading = false;
          if (res.success) {
            this.treeData = res.result;
          }
        });
      } else {
        // 为空重新加载
        this.getAllDict();
      }
    },
    selectTree(v) {
      if (v.length > 0) {
        this.$refs.dictForm.resetFields();
        // 转换null为""
        for (let attr in v[0]) {
          if (v[0][attr] == null) {
            v[0][attr] = "";
          }
        }
        let str = JSON.stringify(v[0]);
        let data = JSON.parse(str);
        this.selectNode = data;
        this.dictForm = data;
        this.editTitle = data.title + "(" + data.type + ")";
        // 重新加载表
        this.searchForm.pageNumber = 1;
        this.searchForm.pageSize = 10;
        this.getDataList();
      } else {
        this.cancelEdit();
      }
    },
    cancelEdit() {
      let data = this.$refs.tree.getSelectedNodes()[0];
      if (data) {
        data.selected = false;
      }
      // 取消选择后获取全部数据
      this.selectNode = {};
      this.editTitle = "";
      this.getDataList();
    },
    changeSelect(v) {
      this.selectCount = v.length;
      this.selectList = v;
    },
    changeExpand() {
      this.expand = !this.expand;
      if (this.expand) {
        this.expandIcon = "md-arrow-round-back";
        this.span = 19;
      } else {
        this.expandIcon = "md-arrow-round-forward";
        this.span = 23;
      }
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    getDataList() {
      this.loading = true;
      if (this.selectNode.id) {
        this.searchForm.dictId = this.selectNode.id;
      } else {
        delete this.searchForm.dictId;
      }
      if (!this.searchForm.status) {
        this.searchForm.status = "";
      }
      getAllDictDataList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.data = res.result.records;
          this.total = res.result.total;
        }
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      this.getDataList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 10;
      // 重新加载数据
      this.getDataList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order == "normal") {
        this.searchForm.order = "";
      }
      this.getDataList();
    },
    showSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    refreshDict() {
      this.getAllDict();
      this.selectNode = {};
      this.editTitle = "";
      this.getDataList();
    },
    addDcit() {
      this.modalType = 0;
      this.dictModalTitle = "添加字典";
      this.$refs.dictForm.resetFields();
      this.dictModalVisible = true;
    },
    editDcit() {
      this.modalType = 1;
      this.dictModalTitle = "编辑字典";
      this.dictModalVisible = true;
    },
    delDcit() {
      if (!this.selectNode.id) {
        this.$Message.warning("您还未选择要删除的字典");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        loading: true,
        content: "您确认要删除 " + this.selectNode.title + " ?",
        onOk: () => {
          // 删除
          deleteDict({
            ids: this.selectNode.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.refreshDict();
            }
          });
        }
      });
    },
    add() {
      if (!this.selectNode.id) {
        this.$Message.warning("请先选择一个字典类别");
        return;
      }
      this.modalType = 0;
      this.modalTitle = "添加字典 " + this.editTitle + " 的数据";
      this.$refs.form.resetFields();
      this.modalVisible = true;
    },
    edit(v) {
      this.modalType = 1;
      this.modalTitle = "编辑字典 " + this.editTitle + " 的数据";
      this.$refs.form.resetFields();
      // 转换null为""
      for (let attr in v) {
        if (v[attr] == null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      this.form = data;
      this.modalVisible = true;
    },
    handelSubmitDict() {
      this.$refs.dictForm.validate(valid => {
        if (valid) {
          this.submitLoading = true;
          if (this.modalType == 0) {
            // 添加 避免编辑后传入id等数据 记得删除
            delete this.dictForm.id;
            addDict(this.dictForm).then(res => {
              this.submitLoading = false;
              if (res.success) {
                this.$Message.success("操作成功");
                this.getAllDict();
                this.dictModalVisible = false;
              }
            });
          } else if (this.modalType == 1) {
            // 编辑
            editDict(this.dictForm).then(res => {
              this.submitLoading = false;
              if (res.success) {
                this.$Message.success("操作成功");
                this.getAllDict();
                this.dictModalVisible = false;
              }
            });
          }
        }
      });
    },
    handelSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          this.submitLoading = true;
          if (this.modalType == 0) {
            // 添加 避免编辑后传入id等数据 记得删除
            delete this.form.id;
            this.form.dictId = this.selectNode.id;
            addDictData(this.form).then(res => {
              this.submitLoading = false;
              if (res.success) {
                this.$Message.success("操作成功");
                this.getDataList();
                this.modalVisible = false;
              }
            });
          } else if (this.modalType == 1) {
            // 编辑
            editDictData(this.form).then(res => {
              this.submitLoading = false;
              if (res.success) {
                this.$Message.success("操作成功");
                this.getDataList();
                this.modalVisible = false;
              }
            });
          }
        }
      });
    },
    remove(v) {
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除 " + v.title + " ?",
        loading: true,
        onOk: () => {
          // 删除
          deleteData({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.getDataList();
            }
          });
        }
      });
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          // 批量删除
          deleteData({
            ids: ids
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.clearSelectAll();
              this.getDataList();
            }
          });
        }
      });
    }
  },
  mounted() {
    // 计算高度
    let height = document.documentElement.clientHeight;
    this.maxHeight = Number(height - 287) + "px";
    this.init();
  }
};
