        tooltip: true,
        sortable: false
      }, {
        title: "请假理由",
        key: "leaveReason",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "请假状态",
        key: "status",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "审批人",
        key: "auditUser",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "申请时间",
        key: "dateTime",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "创建者",
        key: "createBy",
        sortable: true,
        sortType: "desc",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "修改时间",
        key: "updateTime",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "修改者",
        key: "updateBy",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "操作",
        key: "action",
        align: "center",
        width: 400,
        render: (h, params) => {
          return h("div", [h("Button", {
            props: {
              type: "success",
              size: "small",
              icon: "ios-build",
              ghost: true,
              disabled: params.row.status != '未审核'
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.auditByIdsFx(params.row);
              }
            }
          }, "通过"), h("Button", {
            props: {
              type: "error",
              size: "small",
              icon: "ios-bug",
              ghost: true,
              disabled: params.row.status != '未审核'
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.notAuditByIdsFx(params.row);
              }
            }
          }, "驳回"), h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true,
              ghost: true,
              disabled: params.row.status != '未审核'
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.edit(params.row);
              }
            }
          }, "编辑"), h("Button", {
            props: {
              type: "error",
              size: "small",
              icon: "md-trash",
              ghost: true,
              ghost: true,
              disabled: params.row.status != '未审核'
            },
            on: {
              click: () => {
                this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      // 表单数据
      pageNumber: 1,
      // 当前页数
      pageSize: 10,
      // 页面大小
      total: 0,
      // 表单数据总数
      showFilterPanelFlag: false
    };
  },
  methods: {
    init() {
      this.getDataList();
    },
    submited() {
      this.currView = "index";
      this.getDataList();
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    rowClick(row, index) {
      this.selectRow = row;
    },
    rowClassNmae(row, index) {
      if (row.id == this.selectRow.id) {
        return "rowClassNmaeColor";
      }
      return "";
    },
    excelData() {
      this.$refs.table.exportCsv({
        filename: "导出结果"
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      this.getDataList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      // 重新加载数据
      this.getDataList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getDataList();
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    getDataList() {
      this.loading = true;
      getUserLeaveList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.data = res.result.records;
          this.total = res.result.total;
        }
      });
    },
    add() {
      this.currView = "add";
    },
    edit(v) {
      // 转换null为""
      for (let attr in v) {
        if (v[attr] == null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      this.formData = data;
      this.currView = "edit";
    },
    remove(v) {
      this.$Modal.confirm({
        title: "确认删除",
        // 记得确认修改此处
        content: "您确认要删除?",
        loading: true,
        onOk: () => {
          // 删除
          deleteUserLeave({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.getDataList();
            }
          });
        }
      });
    },
    auditByIdsFx(v) {
      this.$Modal.confirm({
        title: "确认审核通过",
        content: "您确认要审核通过?",
        loading: true,
        onOk: () => {
          // 删除
          auditByIds({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("审核成功");
              this.getDataList();
            }
          });
        }
      });
    },
    notAuditByIdsFx(v) {
      this.$Modal.confirm({
        title: "确认审核驳回",
        content: "您确认要审核驳回?",
        loading: true,
        onOk: () => {
          notAuditByIds({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("驳回成功");
              this.getDataList();
            }
          });
        }
      });
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          // 批量删除
          deleteUserLeave({
            ids: ids
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.clearSelectAll();
              this.getDataList();
            }
          });
        }
      });
    }
  },
  mounted() {
    this.init();
    this.tableHeight = Number(window.innerHeight - 273);
    this.mycolumns = this.columns;
    let showcolumns = [];
    for (var i = 0; i < this.selected.length; i++) {
      var item = this.selected[i];
      for (var j = 0; j < this.columns.length; j++) {
        if (this.columns[j].title == item) {
          showcolumns.push(this.columns[j]);
        }
      }
    }
    this.columns = showcolumns;
  },
  watch: {
    selected: function (newcolumns) {
      let showcolumns = [];
      for (var i = 0; i < this.mycolumns.length; i++) {
        var item = this.mycolumns[i];
        if (item.title == undefined) showcolumns.push(item);else if (newcolumns.contains(item.title)) showcolumns.push(item);
      }
      this.columns = showcolumns;
    }
  }
};
