import { getTeacherList, deleteTeacher } from "./api.js";
import add from "./add.vue";
import edit from "./edit.vue";
import dict from "@/views/template/dict";
import Viewer from "viewerjs";
export default {
  name: "single-window",
  components: {
    add,
    edit,
    dict
  },
  data() {
    return {
      tableHeight: 0,
      selected: ["选择", "序号", "姓名", "学历", "年龄", "毕业院校", "工资", "在职状态", "签名", "个人简历", "备注", "创建时间", "创建者", "修改时间", "修改者", "操作"],
      modal1: false,
      openSearch: true,
      // 显示搜索
      openTip: true,
      // 显示提示
      formData: {},
      currView: "index",
      loading: true,
      // 表单加载状态
      searchForm: {
        // 搜索框初始化对象
        pageNumber: 1,
        // 当前页数
        pageSize: 15,
        // 页面大小
        sort: "createTime",
        // 默认排序字段
        order: "desc" // 默认排序方式
      },
      selectList: [],
      // 多选数据
      selectCount: 0,
      // 多选计数
      selectRow: 0,
      columns: [
      // 表头
      {
        type: "selection",
        width: 60,
        title: "选择",
        align: "center",
        fixed: "left"
      }, {
        title: "序号",
        width: 85,
        align: "center",
        fixed: "left",
        sortType: true,
        render: (h, params) => {
          return h("span", params.index + (this.searchForm.pageNumber - 1) * this.searchForm.pageSize + 1);
        }
      }, {
        title: "姓名",
        key: "name",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "学历",
        key: "education",
        minWidth: 120,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          if (params.row.education == "博士") {
            return h("div", [h("Tag", {
              props: {
                color: "volcano"
              }
            }, params.row.education)]);
          } else if (params.row.education == "硕士") {
            return h("div", [h("Tag", {
              props: {
                color: "geekblue"
              }
            }, params.row.education)]);
          } else if (params.row.education == "本科") {
            return h("div", [h("Tag", {
              props: {
                color: "cyan"
              }
            }, params.row.education)]);
          } else {
            return h("div", [params.row.education]);
          }
        }
      }, {
        title: "年龄",
        key: "age",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "毕业院校",
        key: "graduated",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "工资",
        key: "wages",
        minWidth: 120,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          return h("div", [h("span", {
            style: {
              color: params.row.wages < 5000 ? "#3CB371" : "#ff0000"
            }
          }, params.row.wages)]);
        }
      }, {
        title: "在职状态",
        key: "status",
        minWidth: 120,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          return h("i-switch", {
            props: {
              size: "large",
              value: params.row.status === "在职"
            },
            scopedSlots: {
              open: () => h("span", "在职"),
              close: () => h("span", "离职")
            },
            on: {
              "on-change": value => {
                // 模拟事件
                this.$Message.success(params.row.name + " 模拟事件成功 :" + value);
              }
            }
          });
        }
      }, {
        title: "签名",
        key: "autograph",
        minWidth: 120,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          if (params.row.autograph == undefined || params.row.autograph == "") {
            return h("div", [h("span", {
              style: {
                color: "#ff9900"
              }
            }, "未上传")]);
          } else {
            return h("img", {
              attrs: {
                src: params.row.autograph
              },
              style: {
                cursor: "zoom-in",
                width: "60px",
                margin: "10px 0",
                "object-fit": "contain"
              },
              on: {
                click: () => {
                  this.showPic(params.row.autograph);
                }
              }
            });
          }
        }
      }, {
        title: "个人简历",
        key: "resume",
        minWidth: 120,
        tooltip: true,
        sortable: false,
        render: (h, params) => {
          if (params.row.resume == undefined || params.row.resume == "") {
            return h("div", [h("span", {
              style: {
                color: "#ff9900"
              }
            }, "未上传")]);
          } else {
            return h("img", {
              attrs: {
                src: require("@/assets/pdf.png")
              },
              style: {
                cursor: "zoom-in",
                width: "60px",
                margin: "10px 0",
                "object-fit": "contain"
              },
              on: {
                click: () => {
                  this.showPdf(params.row.resume);
                }
              }
            });
          }
        }
      }, {
        title: "备注",
        key: "remark",
        minWidth: 120,
        tooltip: true,
        sortable: false
      }, {
        title: "创建时间",
        key: "createTime",
        sortable: true,
        sortType: "desc",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "创建者",
        key: "createBy",
        sortable: true,
        sortType: "desc",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "修改时间",
        key: "updateTime",
        minWidth: 180,
        align: "center",
        tooltip: true
      }, {
        title: "修改者",
        key: "updateBy",
        minWidth: 100,
        align: "center",
        tooltip: true
      }, {
        title: "操作",
        key: "action",
        align: "center",
        width: 200,
        fixed: "right",
        render: (h, params) => {
          return h("div", [h("Button", {
            props: {
              type: "primary",
              size: "small",
              icon: "ios-create-outline",
              ghost: true
            },
            style: {
              marginRight: "5px"
            },
            on: {
              click: () => {
                this.edit(params.row);
              }
            }
          }, "编辑"), h("Button", {
            props: {
              type: "error",
              size: "small",
              icon: "md-trash",
              ghost: true
            },
            on: {
              click: () => {
                this.remove(params.row);
              }
            }
          }, "删除")]);
        }
      }],
      data: [],
      // 表单数据
      pageNumber: 1,
      // 当前页数
      pageSize: 10,
      // 页面大小
      total: 0,
      // 表单数据总数
      showFilterPanelFlag: false
    };
  },
  methods: {
    init() {
      this.getDataList();
    },
    showPic(e) {
      let image = new Image();
      image.src = e;
      let viewer = new Viewer(image, {
        hidden: function () {
          viewer.destroy();
        }
      });
      viewer.show();
    },
    showPdf(e) {
      window.open(e + "?preview=true");
    },
    submited() {
      this.currView = "index";
      this.getDataList();
    },
    changePage(v) {
      this.searchForm.pageNumber = v;
      this.getDataList();
      this.clearSelectAll();
    },
    changePageSize(v) {
      this.searchForm.pageSize = v;
      this.getDataList();
    },
    rowClick(row, index) {
      this.selectRow = row;
    },
    rowClassNmae(row, index) {
      if (row.id == this.selectRow.id) {
        return "rowClassNmaeColor";
      }
      return "";
    },
    excelData() {
      this.$refs.table.exportCsv({
        filename: "导出结果"
      });
    },
    handleSearch() {
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      this.getDataList();
    },
    handleReset() {
      this.$refs.searchForm.resetFields();
      this.searchForm.pageNumber = 1;
      this.searchForm.pageSize = 15;
      this.getDataList();
    },
    changeSort(e) {
      this.searchForm.sort = e.key;
      this.searchForm.order = e.order;
      if (e.order === "normal") {
        this.searchForm.order = "";
      }
      this.getDataList();
    },
    clearSelectAll() {
      this.$refs.table.selectAll(false);
    },
    changeSelect(e) {
      this.selectList = e;
      this.selectCount = e.length;
    },
    getDataList() {
      this.loading = true;
      getTeacherList(this.searchForm).then(res => {
        this.loading = false;
        if (res.success) {
          this.data = res.result.records;
          this.total = res.result.total;
        }
      });
    },
    add() {
      this.currView = "add";
    },
    edit(v) {
      for (let attr in v) {
        if (v[attr] == null) {
          v[attr] = "";
        }
      }
      let str = JSON.stringify(v);
      let data = JSON.parse(str);
      this.formData = data;
      this.currView = "edit";
    },
    remove(v) {
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除 " + v.name + " ?",
        loading: true,
        onOk: () => {
          deleteTeacher({
            ids: v.id
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.getDataList();
            }
          });
        }
      });
    },
    delAll() {
      if (this.selectCount <= 0) {
        this.$Message.warning("您还未选择要删除的数据");
        return;
      }
      this.$Modal.confirm({
        title: "确认删除",
        content: "您确认要删除所选的 " + this.selectCount + " 条数据?",
        loading: true,
        onOk: () => {
          let ids = "";
          this.selectList.forEach(function (e) {
            ids += e.id + ",";
          });
          ids = ids.substring(0, ids.length - 1);
          deleteTeacher({
            ids: ids
          }).then(res => {
            this.$Modal.remove();
            if (res.success) {
              this.$Message.success("操作成功");
              this.clearSelectAll();
              this.getDataList();
            }
          });
        }
      });
    }
  },
  mounted() {
    this.init();
    this.tableHeight = Number(window.innerHeight - 320);
    this.mycolumns = this.columns;
    let showcolumns = [];
    for (var i = 0; i < this.selected.length; i++) {
      var item = this.selected[i];
      for (var j = 0; j < this.columns.length; j++) {
        if (this.columns[j].title == item) {
          showcolumns.push(this.columns[j]);
        }
      }
    }
    this.columns = showcolumns;
  },
  watch: {
    selected: function (newcolumns) {
      let showcolumns = [];
      for (var i = 0; i < this.mycolumns.length; i++) {
        var item = this.mycolumns[i];
        if (item.title == undefined) showcolumns.push(item);else if (newcolumns.contains(item.title)) showcolumns.push(item);
      }
      this.columns = showcolumns;
    }
  }
};
