import { editUserLeave } from "./api.js";
export default {
  name: "edit",
  components: {},
  props: {
    data: Object
  },
  data() {
    return {
      submitLoading: false,
      // 表单提交状态
      form: {
        // 添加或编辑表单对象初始化数据
        leaveDate: "",
        leaveReason: ""
      },
      // 表单验证规则
      formValidate: {},
      tempLeaveDate: ""
    };
  },
  methods: {
    init() {
      this.handleReset();
      this.form = this.data;
      this.tempLeaveDate = this.form.leaveDate;
    },
    changeLeaveDate(e) {
      this.form.leaveDate = e;
    },
    handleReset() {
      this.$refs.form.resetFields();
    },
    handleSubmit() {
      this.$refs.form.validate(valid => {
        if (valid) {
          editUserLeave(this.form).then(res => {
            this.submitLoading = false;
            if (res.success) {
              this.$Message.success("操作成功");
              this.submited();
            }
          });
        }
      });
    },
    close() {
      this.$emit("close", true);
    },
    submited() {
      this.$emit("submited", true);
    }
  },
  mounted() {
    this.init();
  }
};
